mapper.select:
SELECT
  *
FROM USER_INFO
  WHERE CREATE_DATE BETWEEN #{startDate, jdbcType=TIMESTAMP} AND #{endDate, jdbcType=TIMESTAMP}
ORDER BY CREATE_DATE ASC
  LIMIT #{_skiprows}, #{_pagesize}

mapper.insert:
INSERT INTO USER_INFO(
  USER_ID,
  REST_FLAG,
  CREATE_DATE
) VALUES (
  #{userId},
  #{restFlag},
  #{createDate, jdbcType=TIMESTAMP}
)

